const crypto = require('crypto');

// ==========================================
// CONFIGURACI√ìN
// ==========================================
const ASEGURADORA = 'ATLAS';
const FECHA_PROCESO = new Date().toISOString();

// ==========================================
// DICCIONARIOS DE NORMALIZACI√ìN
// ==========================================

const SINONIMOS_MARCAS = {
