};

const GUIONES_CANONICOS = [
    'TYPE-S', 'A-SPEC', 'E-TECH', 'N-LINE', 'GT-LINE',
    'X-LINE', 'R-LINE', 'S-LINE', 'M-SPORT', 'AMG-LINE',
    'F-SPORT', 'GR-SPORT', 'RS-LINE', 'ST-LINE', 'CROSS-COUNTRY',
    'ALL-TERRAIN', 'PLUG-IN', 'E-POWER', 'T-CROSS', 'T-ROC'
];

// ==========================================
// FUNCIONES DE EXTRACCI√ìN DE ESPECIFICACIONES
// ==========================================

function extraerEspecificacionesTecnicas(version, versionCorta, specsSQL = {}) {
    const specs = {
        configuracion_motor: specsSQL.configuracion_motor || null,
        cilindrada_l: specsSQL.cilindrada_l || null,
        traccion: specsSQL.traccion || null,
        numero_puertas: specsSQL.numero_puertas || null,
        tipo_carroceria: specsSQL.tipo_carroceria || null,
        potencia_hp: specsSQL.potencia_hp || null,
        numero_ocupantes: specsSQL.numero_ocupantes || null
    };

    // Si ya tenemos specs del SQL, confiar en ellas pero buscar adicionales
    const texto = `${version || ''} ${versionCorta || ''}`.toUpperCase();

    // N√∫mero de puertas (si no viene del SQL)
    if (!specs.numero_puertas) {
        const puertas = texto.match(/(\d+)\s*PTAS?\b/) || texto.match(/(\d+)P\b/);
        if (puertas) specs.numero_puertas = parseInt(puertas[1], 10);
    }

    // Cilindrada (si no viene del SQL)
    if (!specs.cilindrada_l) {
        const cilindrada = texto.match(/(\d+\.?\d*)\s*[LT](?:\s|$|[^A-Z])/);
        if (cilindrada) specs.cilindrada_l = parseFloat(cilindrada[1]);
    }

    // Tracci√≥n mejorada con sistemas propietarios
    if (!specs.traccion) {
        if (texto.includes('AWD') || texto.includes('QUATTRO') || 
            texto.includes('XDRIVE') || texto.includes('4MATIC') ||
            texto.includes('4MOTION') || texto.includes('SYMMETRICAL AWD')) {
            specs.traccion = 'AWD';
        } else if (texto.includes('FWD')) {
            specs.traccion = 'FWD';
        } else if (texto.includes('RWD')) {
            specs.traccion = 'RWD';
        } else if (texto.includes('4X4') || texto.includes('4WD')) {
            specs.traccion = '4WD';
        }
    }

    // Tipo de carrocer√≠a (si no viene del SQL)
    if (!specs.tipo_carroceria) {
        const carrocerias = {
            'GRAN COUPE': 'GRAN COUPE',
            'COUPE': 'COUPE',
            'SEDAN': 'SEDAN',
            'SUV': 'SUV',
            'HATCHBACK': 'HATCHBACK',
            'SPORTBACK': 'SPORTBACK',
            'WAGON': 'WAGON',
            'PICKUP': 'PICKUP',
            'VAN': 'VAN',
            'CONVERTIBLE': 'CONVERTIBLE',
            'CROSSOVER': 'CROSSOVER',
            'CABRIO': 'CONVERTIBLE',
            'ROADSTER': 'ROADSTER'
        };

        for (const [buscar, valor] of Object.entries(carrocerias)) {
            if (texto.includes(buscar)) {
                specs.tipo_carroceria = valor;
                break;
            }
        }

        // Detectar HB como hatchback
        if (!specs.tipo_carroceria && texto.match(/\bHB\b/)) {
            specs.tipo_carroceria = 'HATCHBACK';
        }
    }

    // Configuraci√≥n del motor (si no viene del SQL)
    if (!specs.configuracion_motor) {
        if (texto.includes(' EV ') || texto.includes(' BEV ') || texto.includes('ELECTRICO')) {
            specs.configuracion_motor = 'ELECTRICO';
        } else if (texto.includes(' PHEV ') || texto.includes('PLUG-IN')) {
            specs.configuracion_motor = 'HIBRIDO_PLUG';
        } else if (texto.includes(' HEV ') || texto.includes(' HYBRID ')) {
            specs.configuracion_motor = 'HIBRIDO';
        } else if (texto.includes('DIESEL') || texto.includes(' TDI ')) {
            specs.configuracion_motor = 'DIESEL';
        } else if (texto.includes('TURBO') || texto.match(/\d+T\b/) || 
                   texto.includes('TSI') || texto.includes('TFSI')) {
            specs.configuracion_motor = 'TURBO';
        } else if (texto.includes(' V8 ')) {
            specs.configuracion_motor = 'V8';
        } else if (texto.includes(' V6 ')) {
            specs.configuracion_motor = 'V6';
        } else if (texto.match(/\b[L|I]4\b/) || texto.includes('4CIL')) {
            specs.configuracion_motor = 'L4';
        } else if (texto.match(/\b[L|I]3\b/) || texto.includes('3CIL')) {
            specs.configuracion_motor = 'L3';
        } else if (texto.match(/\b[L|I]6\b/) || texto.includes('6CIL')) {
            specs.configuracion_motor = 'L6';
        }
    }

    // Potencia (si no viene del SQL)
    if (!specs.potencia_hp) {
        const potencia = texto.match(/(\d+)\s*HP/);
        if (potencia) specs.potencia_hp = parseInt(potencia[1], 10);
    }

    // N√∫mero de ocupantes (si no viene del SQL)
    if (!specs.numero_ocupantes) {
        const ocupantes = texto.match(/(\d+)\s*(?:PAS|OCUP|PLAZAS)/);
        if (ocupantes) {
            specs.numero_ocupantes = parseInt(ocupantes[1], 10);
        } else if (specs.numero_puertas) {
            // Estimaci√≥n basada en puertas
            specs.numero_ocupantes = specs.numero_puertas === 2 ? 2 : 5;
        }
    }

    return specs;
}

// ==========================================
// FUNCIONES DE LIMPIEZA Y NORMALIZACI√ìN
// ==========================================

function limpiarPrefijos(version, marca, modelo) {
    if (!version) return '';

    let versionLimpia = version.toUpperCase().trim();
    const marcaUpper = marca ? marca.toUpperCase() : '';
    const modeloUpper = modelo ? modelo.toUpperCase() : '';

    // Para GENERAL MOTORS, limpiar las marcas reales
    if (marca === 'GENERAL MOTORS') {
        const marcasGM = ['CHEVROLET', 'BUICK', 'CADILLAC', 'GMC'];
        for (const marcaGM of marcasGM) {
            if (versionLimpia.startsWith(marcaGM + ' ')) {
                // Detectar la marca real para la normalizaci√≥n
                if (marcaGM === 'CHEVROLET') {
                    versionLimpia = versionLimpia.substring(10).trim(); // Quitar "CHEVROLET "
                } else if (marcaGM === 'BUICK') {
                    versionLimpia = versionLimpia.substring(6).trim(); // Quitar "BUICK "
                } else if (marcaGM === 'CADILLAC') {
                    versionLimpia = versionLimpia.substring(9).trim(); // Quitar "CADILLAC "
                } else if (marcaGM === 'GMC') {
                    versionLimpia = versionLimpia.substring(4).trim(); // Quitar "GMC "
                }
                break;
            }
        }
    }

    // Para VOLKSWAGEN, limpiar "VW"
    if (marca === 'VOLKSWAGEN' && versionLimpia.startsWith('VW ')) {
        versionLimpia = versionLimpia.substring(3).trim();
    }

    // Patr√≥n 1: C√≥digo de 2 letras + marca + modelo
    const patronCodigo = new RegExp('^[A-Z]{2}\\s+' + marcaUpper + '\\s+' + modeloUpper + '\\s+');
    if (patronCodigo.test(versionLimpia)) {
        versionLimpia = versionLimpia.replace(patronCodigo, '');
    }

    // Patr√≥n 2: Marca + modelo
    const patronMarca = new RegExp('^' + marcaUpper + '\\s+' + modeloUpper + '\\s+');
    if (patronMarca.test(versionLimpia)) {
        versionLimpia = versionLimpia.replace(patronMarca, '');
    }

    // Patr√≥n 3: Solo modelo
    const patronModelo = new RegExp('^' + modeloUpper + '\\s+');
    if (patronModelo.test(versionLimpia)) {
        versionLimpia = versionLimpia.replace(patronModelo, '');
    }

    // Limpiar modelo del inicio si aparece nuevamente
    if (modeloUpper && versionLimpia.startsWith(modeloUpper + ' ')) {
        versionLimpia = versionLimpia.substring(modeloUpper.length + 1).trim();
    }

    return versionLimpia.trim();
}

function normalizarMarca(marca, versionCompleta) {
    if (!marca) return '';

    // Para GENERAL MOTORS, detectar la marca real desde la versi√≥n
    if (marca === 'GENERAL MOTORS' && versionCompleta) {
        const versionUpper = versionCompleta.toUpperCase();
        if (versionUpper.startsWith('CHEVROLET')) return 'CHEVROLET';
        if (versionUpper.startsWith('BUICK')) return 'BUICK';
        if (versionUpper.startsWith('CADILLAC')) return 'CADILLAC';
        if (versionUpper.startsWith('GMC')) return 'GMC';
        return 'CHEVROLET'; // Default para GM
    }

    let marcaNorm = marca.toUpperCase().trim();
    marcaNorm = marcaNorm.replace(/[^\w\s-]/g, ' ').replace(/\s+/g, ' ').trim();

    return SINONIMOS_MARCAS[marcaNorm] || marcaNorm;
}

function normalizarModelo(modelo, marca) {
    if (!modelo) return '';

    let modeloNorm = modelo.toUpperCase().trim();
    modeloNorm = modeloNorm.replace(/[^\w\s-]/g, ' ').replace(/\s+/g, ' ').trim();

    // Limpiar redundancia: si el modelo empieza con la marca, eliminarla
    if (marca && modeloNorm.startsWith(marca.toUpperCase() + ' ')) {
        modeloNorm = modeloNorm.substring(marca.length + 1).trim();
    }

    return modeloNorm;
}

function normalizarTransmision(codigoTransmision, descripcionTransmision, descripcionVersion) {
    // Priorizar descripci√≥n sobre c√≥digo
    const desc = `${descripcionTransmision || ''} ${descripcionVersion || ''}`.toUpperCase();

    // Detectar patrones de transmisi√≥n en el texto
    if (desc.match(/\b(MANUAL|STD|MAN|MT|ESTANDAR)\b/)) return 'MANUAL';
    if (desc.match(/\b(AUT|AUTO|AUTOMATICA|AUTOM√ÅTICA|AUTOMATIC|AT)\b/)) return 'AUTO';
    if (desc.match(/\b(CVT|DCT|DSG|PDK|S TRONIC|STRONIC|TIPTRONIC)\b/)) return 'AUTO';

    // Si no hay info en texto, usar c√≥digo
    const codigo = String(codigoTransmision);
    return TRANSMISION_BINARIA[codigo] || 'AUTO';
}

function normalizarVersion(versionCompleta, versionCorta, marca, modelo, transmisionNorm) {
    if (!versionCompleta && !versionCorta) return 'STANDARD';

    // Limpiar prefijos
    let versionBase = limpiarPrefijos(versionCompleta || versionCorta, marca, modelo);

    // Si queda vac√≠o despu√©s de limpiar, usar version corta
    if (!versionBase && versionCorta) {
        versionBase = versionCorta.toUpperCase().trim();
    }

    if (!versionBase) return 'STANDARD';

    // Limpiar transmisi√≥n y extras del final
    versionBase = versionBase
        .replace(/\b(AUT|AUTOMATICA|AUTOMATIC|AUTO|MANUAL|STD|CVT|DCT|DSG|PDK)\b/gi, '')
        .replace(/\s+/g, ' ')
        .trim();

    // Aplicar guiones can√≥nicos
    GUIONES_CANONICOS.forEach(termino => {
        const sinGuion = termino.replace('-', '');
        const regex = new RegExp('\\b' + sinGuion + '\\b', 'gi');
        versionBase = versionBase.replace(regex, termino);
    });

    // Limpiar extras de equipamiento comunes
    const extras = [
        'PIEL', 'TELA', 'QUEMACOCOS', 'SUNROOF', 'NAVEGACION', 'GPS',
        'CAMARA', 'SENSORES', 'BLUETOOTH', 'USB', 'ANDROID', 'CARPLAY',
        'BOSE', 'HARMAN', 'SOUND', 'AUDIO', 'PREMIUM', 'LUXURY'
    ];

    extras.forEach(extra => {
        versionBase = versionBase.replace(new RegExp('\\b' + extra + '\\b', 'gi'), '');
    });

    // Limpiar espacios m√∫ltiples
    versionBase = versionBase.replace(/\s+/g, ' ').trim();

    return versionBase || 'STANDARD';
}

// ==========================================
// FUNCIONES DE GENERACI√ìN DE HASHES
// ==========================================

function generarHashComercial(marca, modelo, a√±o, version, transmision) {
    const componentes = [
        marca.toUpperCase().trim(),
        modelo.toUpperCase().trim(),
        String(a√±o),
        version.toUpperCase().trim(),
        transmision.toUpperCase().trim()
    ];

    const stringParaHash = componentes.join('|');
    return crypto.createHash('sha256').update(stringParaHash).digest('hex');
}

function generarHashTecnico(marca, modelo, a√±o, version, transmision, specs) {
    const specsOrdenadas = [
        specs.configuracion_motor || '',
        specs.cilindrada_l ? String(specs.cilindrada_l) : '',
        specs.traccion || '',
        specs.numero_puertas ? String(specs.numero_puertas) : '',
        specs.tipo_carroceria || '',
        specs.potencia_hp ? String(specs.potencia_hp) : '',
        specs.numero_ocupantes ? String(specs.numero_ocupantes) : ''
    ];

    const stringParaHash = [
        marca.toUpperCase().trim(),
        modelo.toUpperCase().trim(),
        String(a√±o),
        version.toUpperCase().trim(),
        transmision.toUpperCase().trim(),
        ...specsOrdenadas
    ].join('|');

    return crypto.createHash('sha256').update(stringParaHash).digest('hex');
}

function generarSpecSignature(specs) {
    const partes = [];

    if (specs.configuracion_motor) partes.push(specs.configuracion_motor);
    if (specs.cilindrada_l) partes.push(`${specs.cilindrada_l}L`);
    if (specs.traccion) partes.push(specs.traccion);
    if (specs.numero_puertas) partes.push(`${specs.numero_puertas}P`);
    if (specs.tipo_carroceria) partes.push(specs.tipo_carroceria);
    if (specs.potencia_hp) partes.push(`${specs.potencia_hp}HP`);
    if (specs.numero_ocupantes) partes.push(`${specs.numero_ocupantes}PAX`);

    return partes.join('-') || 'BASE';
}

// ==========================================
// PROCESAMIENTO PRINCIPAL
// ==========================================

const inputData = $input.all();
const registros = [];
const rechazados = [];
const estadisticas = {
    total: inputData.length,
    validos: 0,
    rechazados: 0,
    conSpecs: 0,
    conTodasSpecs: 0,
    specsExtraidas: {
        configuracion_motor: 0,
        cilindrada_l: 0,
        traccion: 0,
        numero_puertas: 0,
        tipo_carroceria: 0,
        potencia_hp: 0,
        numero_ocupantes: 0
    }
};

console.log(`\nüöÄ Iniciando procesamiento ${ASEGURADORA} v3.0`);
console.log(`   Registros a procesar: ${inputData.length}`);

for (const item of inputData) {
    const data = item.json;

    // Validaci√≥n estricta de campos obligatorios
    if (!data.marca || !data.modelo || !data.a√±o || 
        !data.version_completa || data.transmision_codigo === null) {
        rechazados.push({
            registro: data,
            razon: `Faltan campos obligatorios: ${!data.marca ? 'marca ' : ''}${!data.modelo ? 'modelo ' : ''}${!data.a√±o ? 'a√±o ' : ''}${!data.version_completa ? 'version ' : ''}${data.transmision_codigo === null ? 'transmision' : ''}`
        });
        estadisticas.rechazados++;
        continue;
    }

    // Normalizaci√≥n de marca
    const marcaNormalizada = normalizarMarca(data.marca, data.version_completa);

    // Normalizaci√≥n de modelo
    const modeloNormalizado = normalizarModelo(data.modelo, marcaNormalizada);

    // Normalizaci√≥n de transmisi√≥n (binaria)
    const transmisionNormalizada = normalizarTransmision(
        data.transmision_codigo,
        data.transmision_descripcion,
        data.version_completa
    );

    // Normalizaci√≥n de versi√≥n
    const versionNormalizada = normalizarVersion(
        data.version_completa,
        data.version_corta,
        marcaNormalizada,
        modeloNormalizado,
        transmisionNormalizada
    );

    // Extracci√≥n de especificaciones t√©cnicas (combinando SQL y JS)
    const specsSQL = {
        configuracion_motor: data.configuracion_motor,
        cilindrada_l: data.cilindrada_l,
        traccion: data.traccion,
        numero_puertas: data.numero_puertas,
        tipo_carroceria: data.tipo_carroceria,
        potencia_hp: data.potencia_hp,
        numero_ocupantes: data.numero_ocupantes
    };

    const specs = extraerEspecificacionesTecnicas(
        data.version_completa,
        data.version_corta,
        specsSQL
    );

    // Contar especificaciones extra√≠das
    let specsCount = 0;
    for (const [key, value] of Object.entries(specs)) {
        if (value !== null) {
            specsCount++;
            estadisticas.specsExtraidas[key]++;
        }
    }

    if (specsCount > 0) estadisticas.conSpecs++;
    if (specsCount === 7) estadisticas.conTodasSpecs++;

    // Generar spec signature
    const specSignature = generarSpecSignature(specs);

    // Generar hashes
    const hashComercial = generarHashComercial(
        marcaNormalizada,
        modeloNormalizado,
        data.a√±o,
        versionNormalizada,
        transmisionNormalizada
    );

    const hashTecnico = generarHashTecnico(
        marcaNormalizada,
        modeloNormalizado,
        data.a√±o,
        versionNormalizada,
        transmisionNormalizada,
        specs
    );

    // Crear registro de salida para public.catalogo_homologado
    const registro = {
        // Hashes
        hash_comercial: hashComercial,
        hash_tecnico: hashTecnico,

        // Datos fuente (sin normalizar)
        marca: data.marca,
        modelo: data.modelo,
        a√±o: data.a√±o,
        version: data.version_completa,
        version_corta: data.version_corta || null,
        transmision_codigo: data.transmision_codigo,
        transmision_descripcion: data.transmision_descripcion,

        // Normalizados (comerciales)
        marca_normalizada: marcaNormalizada,
        modelo_normalizado: modeloNormalizado,
        version_normalizada: versionNormalizada,
        transmision_normalizada: transmisionNormalizada,

        // Especificaciones t√©cnicas
        configuracion_motor: specs.configuracion_motor,
        cilindrada_l: specs.cilindrada_l,
        traccion: specs.traccion,
        numero_puertas: specs.numero_puertas,
        tipo_carroceria: specs.tipo_carroceria,
        potencia_hp: specs.potencia_hp,
        numero_ocupantes: specs.numero_ocupantes,
        spec_signature: specSignature,

        // Metadata
        aseguradoras_disponibles: {
            [ASEGURADORA]: {
                id_original: data.id_original,
                activo: data.activo,
                fecha_actualizacion: data.fecha_actualizacion
            }
        },
        catalogo_marca_id: data.catalogo_marca_id,
        catalogo_modelo_id: data.catalogo_modelo_id,

        // Componentes de los hashes para auditor√≠a
        hash_components_comercial: {
            marca: marcaNormalizada,
            modelo: modeloNormalizado,
            a√±o: String(data.a√±o),
            version: versionNormalizada,
            transmision: transmisionNormalizada
        },
        hash_components_tecnico: {
            marca: marcaNormalizada,
            modelo: modeloNormalizado,
            a√±o: String(data.a√±o),
            version: versionNormalizada,
            transmision: transmisionNormalizada,
            specs: specs
        }
    };

    registros.push(registro);
    estadisticas.validos++;
