const crypto = require('crypto');

// ==========================================
// CONFIGURACIÃ“N ESPECÃFICA PARA CHUBB
// ==========================================
const ASEGURADORA = 'CHUBB';

// ==========================================
// DICCIONARIOS DE NORMALIZACIÃ“N
// ==========================================

const SINONIMOS_MARCAS = {
    'MERCEDES BENZ': 'MERCEDES-BENZ',
    'MERCEDES': 'MERCEDES-BENZ',
    'LAND ROVER': 'LAND ROVER',
    'VW': 'VOLKSWAGEN',
    'CHEV': 'CHEVROLET',
    'CHEVY': 'CHEVROLET',
    'GMC': 'GMC',
    'GENERAL MOTORS': 'CHEVROLET',
    'CHRYSLER': 'CHRYSLER',
    'ALFA': 'ALFA ROMEO',  // Solo normaliza "ALFA" a "ALFA ROMEO"
    // NO incluir 'ALFA ROMEO': 'ALFA ROMEO' porque ya es correcto
    // NO incluir 'VOLKSWAGEN': 'VOLKSWAGEN' porque ya es correcto
    'BMW': 'BMW',
    'MINI': 'MINI',
    'AUDI': 'AUDI',
    'LINCOLN': 'LINCOLN',
    'JAC': 'JAC',
    'FIAT': 'FIAT',
    'MAZDA': 'MAZDA',
    'TOYOTA': 'TOYOTA',
    'HONDA': 'HONDA',
    'SEAT': 'SEAT',
    'PEUGEOT': 'PEUGEOT',
    'RENAULT': 'RENAULT',
    'VOLVO': 'VOLVO',
    'FORD': 'FORD',
    'NISSAN': 'NISSAN',
    'MITSUBISHI': 'MITSUBISHI',
    'SUZUKI': 'SUZUKI',
    'PORSCHE': 'PORSCHE',
    'ISUZU': 'ISUZU',
    'REMOLQUES': 'REMOLQUES',
    'BAIC': 'BAIC',
    'ACURA': 'ACURA',
    'FERRARI': 'FERRARI',
    'LAMBORGHINI': 'LAMBORGHINI',
    'BENTLEY': 'BENTLEY',
    'ASTON MARTIN': 'ASTON MARTIN',
    'INFINITI': 'INFINITI',
    'CUPRA': 'CUPRA'
};

// Mapeo de transmisiones basado en cÃ³digos de Chubb
const MAPA_TRANSMISIONES = {
    1: 'MANUAL',
    2: 'AUTO',
    0: 'MANUAL',
    null: 'NO_ESPECIFICADA',
    undefined: 'NO_ESPECIFICADA'
};

// ==========================================
// FUNCIONES DE NORMALIZACIÃ“N
// ==========================================

function normalizarTexto(texto) {
    if (!texto) return '';
    return texto
        .toUpperCase()
        .normalize('NFD')
        .replace(/[\u0300-\u036f]/g, '')
        .replace(/[^A-Z0-9\s-]/g, '')
        .replace(/\s+/g, ' ')
        .trim();
}

function normalizarMarca(marca) {
    if (!marca) return 'SIN_MARCA';
    const marcaNorm = normalizarTexto(marca);
    return SINONIMOS_MARCAS[marcaNorm] || marcaNorm;
}

function normalizarModelo(modelo, marca) {
    if (!modelo) return 'SIN_MODELO';

    let modeloNorm = normalizarTexto(modelo);
    const marcaNorm = normalizarTexto(marca);

    // Eliminar la marca del inicio del modelo si estÃ¡ presente
    if (modeloNorm.startsWith(marcaNorm + ' ')) {
        modeloNorm = modeloNorm.substring(marcaNorm.length + 1);
    }

    return modeloNorm;
}

function normalizarVersion(versionCompleta, versionCorta) {
    // Preferir versiÃ³n corta si estÃ¡ disponible
    const version = versionCorta || versionCompleta;
    if (!version) return 'BASICA';

    // Normalizar y extraer la parte principal de la versiÃ³n
    let versionNorm = normalizarTexto(version);

    // Remover cÃ³digos tÃ©cnicos comunes al final
    versionNorm = versionNorm
        .replace(/\s+(L[3-8]|V[6-8]|V1[02]|I[4-6]).*$/, '')
        .replace(/\s+(AUT|STD|CVT|PDK|DSG|TIPTRONIC).*$/, '')
        .replace(/\s+[0-9]\s+(ABS|D\/T).*$/, '')
        .replace(/\s+(IMP|IEM|IMO|ISM|FSI|MPFI).*$/, '')
        .trim();

    return versionNorm || 'BASICA';
}

function normalizarTransmision(codigoTransmision, descripcionVersion) {
    // Primero usar el cÃ³digo de transmisiÃ³n si estÃ¡ disponible
    if (codigoTransmision !== null && codigoTransmision !== undefined) {
        const mapped = MAPA_TRANSMISIONES[codigoTransmision];
        if (mapped && mapped !== 'NO_ESPECIFICADA') {
            return mapped;
        }
    }

    // Si no hay cÃ³digo vÃ¡lido, intentar extraer de la descripciÃ³n
    if (descripcionVersion) {
        const versionUpper = descripcionVersion.toUpperCase();

        // Buscar patrones de transmisiÃ³n automÃ¡tica
        if (versionUpper.includes('AUT') || 
            versionUpper.includes('CVT') || 
            versionUpper.includes('PDK') ||
            versionUpper.includes('DSG') ||
            versionUpper.includes('TIPTRONIC') ||
            versionUpper.includes('S-TRONIC') ||
            versionUpper.includes('XTRONIC') ||
            versionUpper.includes('DCT')) {
            return 'AUTO';
        }

        // Buscar patrones de transmisiÃ³n manual
        if (versionUpper.includes('STD') || 
            versionUpper.includes('MANUAL')) {
            return 'MANUAL';
        }
    }

    return 'NO_ESPECIFICADA';
}

// ==========================================
// FUNCIONES DE EXTRACCIÃ“N DE ESPECIFICACIONES
// ==========================================

function extraerEspecificaciones(versionCompleta, ocupantes) {
    const especificaciones = {
        configuracion_motor: null,
        cilindrada_l: null,
        potencia_hp: null,
        numero_puertas: null,
        tipo_carroceria: null,
        traccion: null,
        numero_ocupantes: ocupantes || null
    };

    if (!versionCompleta) return especificaciones;

    const version = versionCompleta.toUpperCase();

    // Extraer configuraciÃ³n de motor
    const motorMatch = version.match(/\b(L[3-8]|V[6-8]|V1[02]|I[4-6]|W1[26])\b/);
    if (motorMatch) {
        especificaciones.configuracion_motor = motorMatch[1];
    } else if (version.match(/\b([3-8])\s*CIL/)) {
        especificaciones.configuracion_motor = 'L' + RegExp.$1;
    }

    // Extraer cilindrada (formato X.X L o X.XL)
    // Evitar confundir con toneladas (T) o turbo (T)
    const cilindradaMatch = version.match(/\b(\d\.\d)\s*L\b/);
    if (cilindradaMatch) {
        especificaciones.cilindrada_l = parseFloat(cilindradaMatch[1]);
    }

    // Extraer potencia (formatos: XXX H.P., XXX HP, XXX H.P, XXX CP, XXX CV)
    const potenciaMatch = version.match(/\b(\d{2,3})\s*(H\.?P\.?|HP|CP|CV)\b/);
    if (potenciaMatch) {
        especificaciones.potencia_hp = parseInt(potenciaMatch[1]);
    }

    // Extraer nÃºmero de puertas
    // PatrÃ³n: nÃºmero seguido de ABS o D/T
    const puertasMatch = version.match(/\b([2-5])\s+(ABS|D\/T)\b/);
    if (puertasMatch) {
        especificaciones.numero_puertas = parseInt(puertasMatch[1]);
    }

    // Extraer tipo de carrocerÃ­a SOLO si estÃ¡ explÃ­citamente mencionado
    // NO inferir basado en modelo
    if (version.includes('SEDAN')) {
        especificaciones.tipo_carroceria = 'SEDAN';
    } else if (version.includes('SUV')) {
        especificaciones.tipo_carroceria = 'SUV';
    } else if (version.includes('HATCHBACK') || version.includes('HB')) {
        especificaciones.tipo_carroceria = 'HATCHBACK';
    } else if (version.includes('COUPE')) {
        especificaciones.tipo_carroceria = 'COUPE';
    } else if (version.includes('CONVERTIBLE') || version.includes('CABRIOLET')) {
        especificaciones.tipo_carroceria = 'CONVERTIBLE';
    } else if (version.includes('PICKUP') || version.includes('PICK UP') || version.includes('PICK-UP')) {
        especificaciones.tipo_carroceria = 'PICKUP';
    } else if (version.includes('VAN')) {
        especificaciones.tipo_carroceria = 'VAN';
    } else if (version.includes('WAGON')) {
        especificaciones.tipo_carroceria = 'WAGON';
    }

    // Extraer tracciÃ³n
    if (version.match(/\b(4X4|4WD|AWD)\b/)) {
        especificaciones.traccion = '4WD';
    } else if (version.match(/\b(QUATTRO|XDRIVE|4MATIC|4MOTION|REALTIME)\b/)) {
        especificaciones.traccion = '4WD';
    } else if (version.includes('FWD')) {
        especificaciones.traccion = 'FWD';
    } else if (version.includes('RWD')) {
        especificaciones.traccion = 'RWD';
    }

    // Ajustar ocupantes si no estÃ¡ disponible y tenemos puertas
    if (!especificaciones.numero_ocupantes && especificaciones.numero_puertas) {
        if (especificaciones.numero_puertas === 2) {
            especificaciones.numero_ocupantes = 4;
        } else if (especificaciones.numero_puertas === 3) {
            especificaciones.numero_ocupantes = 4;
        } else if (especificaciones.numero_puertas === 4) {
            especificaciones.numero_ocupantes = 5;
        } else if (especificaciones.numero_puertas === 5) {
            especificaciones.numero_ocupantes = 5;
        }
    }

    return especificaciones;
}

function generarHashComercial(marca, modelo, aÃ±o, version, transmision) {
    const componentes = [
        marca.toUpperCase(),
        modelo.toUpperCase(),
        aÃ±o.toString(),
        version.toUpperCase(),
        transmision.toUpperCase()
    ];

    const textoHash = componentes.join('|');

    // Generar hash SHA256 y tomar EXACTAMENTE los primeros 16 caracteres hexadecimales
    const hashCompleto = crypto
        .createHash('sha256')
        .update(textoHash, 'utf8')
        .digest('hex');

    // IMPORTANTE: Retornar exactamente 16 caracteres
    return hashCompleto.substring(0, 16);
}

function generarHashTecnico(marca, modelo, aÃ±o, version, transmision, motor, puertas, carroceria, cilindrada, traccion, potencia, ocupantes) {
    const componentes = [
        marca.toUpperCase(),
        modelo.toUpperCase(),
        aÃ±o.toString(),
        version.toUpperCase(),
        transmision.toUpperCase(),
        motor || '',
        puertas || '',
        carroceria || '',
        cilindrada || '',
        traccion || '',
        potencia || '',
        ocupantes || ''
    ];

    const textoHash = componentes.join('|');

    // Generar hash SHA256 y tomar EXACTAMENTE los primeros 16 caracteres hexadecimales
    const hashCompleto = crypto
        .createHash('sha256')
        .update(textoHash, 'utf8')
        .digest('hex');

    // IMPORTANTE: Retornar exactamente 16 caracteres
    return hashCompleto.substring(0, 16);
}

function generarSpecSignature(motor, cilindrada, potencia, puertas, carroceria) {
    const parts = [];

    if (motor) parts.push(motor);
    if (cilindrada) parts.push(`${cilindrada}L`);
    if (potencia) parts.push(`${potencia}HP`);
    if (puertas) parts.push(`${puertas}P`);
    if (carroceria) parts.push(carroceria);

    return parts.length > 0 ? parts.join('|') : null;
}

// ==========================================
// PROCESAMIENTO PRINCIPAL
// ==========================================

const items = $input.all();
const registros = [];
const fechaProceso = new Date().toISOString();

for (const item of items) {
    const data = item.json;

    // Validaciones bÃ¡sicas
    if (!data.marca || !data.modelo || !data.aÃ±o) {
        continue;
    }

    // NormalizaciÃ³n bÃ¡sica
    const marcaNormalizada = normalizarMarca(data.marca);
    const modeloNormalizado = normalizarModelo(data.modelo, data.marca);
    const transmisionNormalizada = normalizarTransmision(
        data.transmision_codigo, 
        data.version_completa
    );

    // Normalizar versiones
    const versionNormalizada = normalizarVersion(
        data.version_completa, 
        data.version_corta
    );

    // Extraer especificaciones tÃ©cnicas
    // Para Chubb, el campo ocupantes viene de la tabla NVehiculo
    const ocupantesTabla = data.ocupantes || data.Ocupantes || null;
    const especificaciones = extraerEspecificaciones(
        data.version_completa,
        ocupantesTabla
    );

    // Generar hash comercial
    const hashComercial = generarHashComercial(
        marcaNormalizada,
        modeloNormalizado,
        data.aÃ±o,
        versionNormalizada,
        transmisionNormalizada
    );

    // Generar hash tÃ©cnico
    const hashTecnico = generarHashTecnico(
        marcaNormalizada,
        modeloNormalizado,
        data.aÃ±o,
        versionNormalizada,
        transmisionNormalizada,
        especificaciones.configuracion_motor,
        especificaciones.numero_puertas,
        especificaciones.tipo_carroceria,
        especificaciones.cilindrada_l,
        especificaciones.traccion,
        especificaciones.potencia_hp,
        especificaciones.numero_ocupantes
    );

    // Generar spec signature
    const specSignature = generarSpecSignature(
        especificaciones.configuracion_motor,
        especificaciones.cilindrada_l,
        especificaciones.potencia_hp,
        especificaciones.numero_puertas,
        especificaciones.tipo_carroceria
    );

    // Crear registro de salida con el schema exacto solicitado
    const registro = {
        origen_schema: ASEGURADORA,
        id_original: data.id_original,
        marca: data.marca,
        modelo: data.modelo,
        aÃ±o: data.aÃ±o,
        version: data.version_completa,
        transmision: transmisionNormalizada,
        marca_normalizada: marcaNormalizada,
        modelo_normalizado: modeloNormalizado,
        version_normalizada: versionNormalizada,
        transmision_normalizada: transmisionNormalizada,
        cilindrada_l: especificaciones.cilindrada_l,
        numero_puertas: especificaciones.numero_puertas,
        tipo_carroceria: especificaciones.tipo_carroceria,
        configuracion_motor: especificaciones.configuracion_motor,
        traccion: especificaciones.traccion,
        potencia_hp: especificaciones.potencia_hp,
        numero_ocupantes: especificaciones.numero_ocupantes,
        hash_comercial: hashComercial,
        hash_tecnico: hashTecnico,
        spec_signature: specSignature,
        hash_components: {
            comercial: [
                marcaNormalizada,
                modeloNormalizado,
                data.aÃ±o,
                versionNormalizada,
                transmisionNormalizada
            ],
            tecnico: [
                marcaNormalizada,
                modeloNormalizado,
                data.aÃ±o,
                versionNormalizada,
                transmisionNormalizada,
                especificaciones.configuracion_motor,
                especificaciones.numero_puertas,
                especificaciones.tipo_carroceria,
                especificaciones.cilindrada_l,
                especificaciones.traccion,
                especificaciones.potencia_hp,
                especificaciones.numero_ocupantes
            ]
        },
        aseguradoras_disponibles: {
            [ASEGURADORA]: {
                activo: data.activo !== undefined ? Boolean(data.activo) : true,
                fecha_actualizacion: data.fecha_actualizacion || fechaProceso,
                catalogo_marca_id: data.catalogo_marca_id || null,
                catalogo_modelo_id: data.catalogo_modelo_id || null
            }
        }
    };

    registros.push(registro);
}

return registros;

// Nodo: Deduplicate by Hash - CRÃTICO para evitar duplicados
const items = $input.all();
const uniqueByHash = new Map();
const duplicates = new Map();

// Procesar todos los items
items.forEach(item => {
  const hash = item.json.hash_unico;

  if (!hash) {
    return;
  }

  if (uniqueByHash.has(hash)) {
    // Contar duplicados
    if (!duplicates.has(hash)) {
      duplicates.set(hash, [uniqueByHash.get(hash)]);
    }
    duplicates.set(hash, [...duplicates.get(hash), item]);
  } else {
    uniqueByHash.set(hash, item);
  }
});

// Reportar duplicados encontrados
if (duplicates.size > 0) {  
  // Mostrar ejemplos de duplicados
  let exampleCount = 0;
  for (const [hash, items] of duplicates.entries()) {
    if (exampleCount >= 5) break;

    console.log(`\nðŸ” Hash duplicado: ${hash.substring(0, 16)}...`);
    console.log(`   Registros: ${items.length}`);

    // Mostrar los primeros 2 registros del duplicado
    items.slice(0, 2).forEach((item, idx) => {
      const data = item.json;
    });

    exampleCount++;
  }

  // AnÃ¡lisis de por quÃ© se duplican
  const duplicateAnalysis = new Map();
  for (const [hash, items] of duplicates.entries()) {
    const firstItem = items[0].json;
    const key = `${firstItem.marca_normalizada}|${firstItem.modelo_normalizado}`;

    if (!duplicateAnalysis.has(key)) {
      duplicateAnalysis.set(key, 0);
