    .slice(0, 10);

  sortedAnalysis.forEach(([key, count]) => {
    console.log(`   ${key}: ${count} hashes duplicados`);
  });
}

// EstadÃ­sticas finales
const finalItems = Array.from(uniqueByHash.values());

return finalItems;
