// FUNCIONES DE NORMALIZACIÃ“N
function normalizarTexto(texto) {
    if (!texto) return '';
    return String(texto)
        .toUpperCase()
        .normalize('NFD')
        .replace(/[\u0300-\u036f]/g, '')
        .replace(/[^A-Z0-9\s]/g, '')
        .replace(/\s+/g, ' ')
        .trim();
}

function normalizarMarca(marca) {
    if (!marca) return '';
    const marcaNormalizada = normalizarTexto(marca);

    for (const [canonical, sinonimos] of Object.entries(MARCAS_CANONICAS)) {
        if (sinonimos.includes(marcaNormalizada) || marcaNormalizada === canonical.replace('-', ' ')) {
            return canonical;
        }
    }
    return marcaNormalizada;
}

function normalizarVersion(version) {
    if (!version) return '';
    return String(version)
        .toUpperCase()
        .normalize('NFD')
        .replace(/[\u0300-\u036f]/g, '')
        // Eliminar informaciÃ³n tÃ©cnica especÃ­fica de GNP
        .replace(/\bL\d+\b/gi, '')                    // L4, L6
        .replace(/\b[VI]\d+\b/gi, '')                 // V6, V8, I4
        .replace(/\b\d+\.\d+\b/gi, '')                // 1.5, 2.0, 2.4, 3.5
        .replace(/\b\d+\s*CIL\.?\b/gi, '')            // 4 CIL
        .replace(/\bAUT\b/gi, '')                     // AUT (automÃ¡tica)
        .replace(/\bSTD\b/gi, '')                     // STD (estÃ¡ndar/manual)
        .replace(/\bAC\b/gi, '')                      // AC (aire acondicionado)
        .replace(/\bVE\b/gi, '')                      // VE
        .replace(/\bQC\b/gi, '')                      // QC
        .replace(/\bCA\b/gi, '')                      // C/A (con aire)
        .replace(/\bCON\s*AIRE\b/gi, '')              // CON AIRE
        .replace(/\bCON\s*A\b/gi, '')                 // CON A
        .replace(/[^A-Z0-9\s-]/g, '')
        .replace(/\s+/g, ' ')
        .trim();
}

function normalizarVersionUniversal(versionCompleta, versionCorta, transmisionNorm) {
    const versionParaNormalizar = (versionCorta && 
                                   versionCorta.trim() !== '' && 
                                   versionCorta !== versionCompleta) 
                                   ? versionCorta : versionCompleta;

    let versionNorm = normalizarVersion(versionParaNormalizar);

    // Extraer versiones comunes de GNP
    const patrones = [
        /\b(TECH|TECHNOLOGY)\b/gi,
        /\b(A\s*SPEC|ASPEC)\b/gi,
        /\b(TYPE\s*S|TYPES)\b/gi,
        /\b(ADVANCE|ADVANCED)\b/gi,
        /\b(SPORT|SPORTY)\b/gi,
        /\b(PREMIUM|PREM)\b/gi,
        /\b(LUXURY|LUX)\b/gi,
        /\b(BASE|BASICO)\b/gi,
        /\b(ELITE|ELI)\b/gi,
        /\b(LIMITED|LTD)\b/gi,
        /\b(TURBO)\b/gi,
        /\b(HYBRID|HIBRIDO)\b/gi
    ];

    for (const patron of patrones) {
        const match = versionNorm.match(patron);
        if (match) {
            let version = match[0].toUpperCase().replace(/\s+/g, '-');
            if (version.includes('TECH')) return 'TECH';
            if (version.includes('A-SPEC') || version.includes('ASPEC')) return 'A-SPEC';
            if (version.includes('TYPE-S') || version.includes('TYPES')) return 'TYPE-S';
            if (version.includes('ADVANCE')) return 'ADVANCE';
            if (version.includes('SPORT')) return 'SPORT';
            if (version.includes('PREMIUM') || version.includes('PREM')) return 'PREMIUM';
            if (version.includes('LUXURY') || version.includes('LUX')) return 'LUXURY';
            if (version.includes('BASE') || version.includes('BASICO')) return 'BASE';
            if (version.includes('ELITE') || version.includes('ELI')) return 'ELITE';
            if (version.includes('LIMITED') || version.includes('LTD')) return 'LIMITED';
            if (version.includes('TURBO')) return 'TURBO';
            if (version.includes('HYBRID') || version.includes('HIBRIDO')) return 'HYBRID';
            return version;
        }
    }

    const palabras = versionNorm.split(' ').filter(p => p.length > 2);
    return palabras.length > 0 ? palabras[0] : 'STANDARD';
}

function normalizarTransmision(transmisionDesc) {
    if (!transmisionDesc) return 'NO_ESPECIFICADA';
    return MAPA_TRANSMISIONES[transmisionDesc] || 'NO_ESPECIFICADA';
}

function generarHash(...componentes) {
    const textoHash = componentes
        .map(c => String(c || '').toUpperCase().trim())
        .join('|');

    return crypto
        .createHash('sha256')
        .update(textoHash, 'utf8')
        .digest('hex');
}

